
public class Assign_op {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=6;
		int b=8;
		System.out.println(a=b);  //8
		System.out.println(a+=b); //16
		System.out.println(a-=b); //8
		System.out.println(a*=b); //64
		System.out.println(a/=b); //8
		System.out.println(a%=b); //0
	}

}
