
public class Bit_op {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=7; //7을 2진수로 바꾸면=>0000....0111
		int b=8; //8을 2진수로 바꾸면=>0000....1000
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~b);

	}

}
