
public class Increase_op {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=0;
		num=num+1;
		System.out.println("num: "+num); //1
		System.out.println("++num: "+(++num));//2
		System.out.println("num++: "+(num++));//2
		System.out.println("num: "+num);//3
		System.out.println("--num: "+(--num));//2
		System.out.println("num--: "+(num--));//2
		System.out.println("num: "+num);//1
	

	}

}
