
public class Shift_op {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=-20;
		int c=10;
		System.out.println(a>>2);
		System.out.println(a<<2);
		System.out.println(b>>2);
		System.out.println(b<<2);
		System.out.println(c>>3);
		System.out.println(c<<3);
		System.out.println(a>>>2);
		System.out.println(b>>>2);

		
	}
}
